// import React from 'react';
import './App.css';
import React, { Component } from 'react';
import Card from './Components/Card.jsx';
import { isDeclareTypeAlias } from '@babel/types';

class Experience extends Component {



    render() {

        const prodigyImage = "https://4yerik.github.io/src/images/prodigy/prodigyLogo.png";
        const details = [
            {
                title: "The Cooperators",
                subtitle: "Software Developer",
                type: "short",
                textOne: "The Cooperators is a Canadian insurance company that was founded in 1945. I worked at The Cooperators for four months, from January 2020 to April 2020.",
                textTwo: "Together with my team, we delivered a claim updates section along with a dropzone for uploading documents. This was done with the goal of reducing calls to the call centre.",
                textThree: "I used React hooks to improve code readability and efficiency. In addition, I used aria attributes and focus controls to meet Web Content Accessibility Guidelines (Level AA).",
            },
            {
                title: "Prodigy",
                subtitle: "Automation Engineer",
                textOne: "Prodigy is an education startup that was founded in 2010 and is currently one of Canada's fastest growing startups. " +
                    "Their main product is an online game which incorporates curriculum based math questions with the intention of helping students love learning. " +
                    "I worked at Prodigy on an application used by school principals for four months, from May 2018 to August 2018.",
                type: "regular",
                textTwo: "At Prodigy, I learned the best practices around testing and used the knowledge to write end to end automation tests with Cypress." +
                    "In addition to writing automation tests, I had the opportunity work on developing Prodigy's homepage redesign. " +
                    "It makes me proud to think that millions of people get to see my work every month.",
                textThree: "In my last few weeks at Prodigy, I worked on developing a Bash script that detects new tests and confirms the tests are stable in the production pipeline. " +
                    "Many of the existing automation tests were flaky, meaning that they sometimes failed and later passed with the same code. " +
                    "This inconsistency caused developers to doubt the validity of a failed test and wasted time with reruns.",
                pictureOne: <img src="https://github.com/4yerik/images/blob/master/prodigy/teamLunch.jpg?raw=true"></img>,
                pictureTwo: <img src="https://github.com/4yerik/images/blob/master/prodigy/prodigyLogo.png?raw=true"></img>,
                pictureThree: <img src="https://github.com/4yerik/images/blob/master/prodigy/lastDay.jpg?raw=true"></img>,
            },

            {
                title: "Private Math Tutor",
                role: "",
                subtitle: "October 2014 - June 2019",
                type: "short",
                textOne: "\"Thank you so much. I'm going to miss you so much next year. You helped me more than my own teacher.\" -Sage",
                textTwo: "\"Thank you for helping us over the past three years. We wish you all the best.\" -Evlin, George's and Julia's mother",
                textThree: "\"Thank you so much for helping Dylan, you were such a big help to him.\" -Laura and Troy, Dylan's parents",
            },

            {
                title: "Canterbury Hills",
                subtitle: "Overnight Camp Leader",
                period: "May 2018 - August 2018",
                textOne: "Canterbury Hills is a camp located in Ancaster, ON that has been operating since 1960. Each week, I was assigned to a new group of campers in a specific age group. Every week was a little different, but in general, I led classic camp activities such as archery, rock climbing, high ropes, campfires, and hiking.",
                type: "regular",
                textTwo: "Working at Canterbury was great leadership experience and a true test of my ability as a role model. " +
                    "It was heartwarming to see campers gain self confidence, try new activities, and develop friendships. " +
                    "Some of my youngest campers suprised me with how independent they were, while other campers suprised me with their ability to empathize.",
                textThree: "Despite 80 hour work weeks, my campers made the summer of 2018 a fulfilling one. " +
                    "Although there were difficult moments, I have countless of positive memories that will last a life time. " +
                    "Working here gave me the chance to give back to the summer camp experience I had loved so much growing up.",
                pictureOne: <img src="https://github.com/4yerik/images/blob/master/canterbury/WeekThreeCanterbury.PNG?raw=true"></img>,
                pictureTwo: <img src="https://github.com/4yerik/images/blob/master/canterbury/StoneWall.jpg?raw=true"></img>,
                pictureThree: <img src="https://github.com/4yerik/images/blob/master/canterbury/WeekSevenCanterbury.PNG?raw=true"></img>,
            },

            {
                title: "Hamilton Wentworth District School Board",
                subtitle: "Summer Camp Counsellor",
                period: "July 2016 - August 2016",
                textOne: "Focus on Youth is a partnership between HWDSB and non-profit organizations that supports inner cities in delivering free or low-cost summer programs and activities.",
                type: "short",
                textTwo: "During the summer of 2016, I was selected to be part of HWDSB's Focus on Youth staff. I was placed at Bennetto Elementary school where I helped the YMCA supervise roughly 40 campers.",
                textThree: " I engaged campers in activities such as basketball, dodgeball, swimming, and crafts. In addition, I promoted the literacy program by helping campers learn how to read.",
            }

        ];


        return (
            <div className="sides pink pb">
                {details.map(detail => (
                    <Card
                        type={detail.type}
                        title={detail.title}
                        subtitle={detail.subtitle}
                        pictureOne={detail.pictureOne}
                        pictureTwo={detail.pictureTwo}
                        pictureThree={detail.pictureThree}
                        textOne={detail.textOne}
                        textTwo={detail.textTwo}
                        textThree={detail.textThree}
                    />
                ))
                }
            </div>

        );
    }
}

export default Experience;
